stylesheet:
/* Stylesheet for printing Project Gutenberg ebooks */
/* The CSS3 code used in this stylesheet will generate errors in the W3C CSS checker: see http://www.w3.org/TR/css3-page/ */

body
{
    color: black;
    background: white;
    font-size: 10pt;
    font-family: Georgia, "Times New Roman", Times, serif;

	text-align: justify;
	// column-count: 2;
}


@page 
{
	size: A4;
	margin: 27mm 16mm 27mm 16mm;
}

@page :left 
{
	@bottom-left 
	{
		content: counter(page);
	}
}

@page :right 
{
	@bottom-right
	{
		content: counter(page);
	}
}


div.div1
{
	page-break-after: always;
}


h2 
{
	string-set: header content();
}


@page :right
{
	@top-right 
	{
		content: string(header, first); 
		font-style: italic;
	}
}

@page :left
{
	@top-left
	{
		content: string(header, first); 
		font-style: italic;
	}
}






a:link, a:visited
{
    color: black;
    background: transparent;
    text-decoration: none;
}


h1 + p, h2 + p, h3 + p, h4 + p, h5 + p, h6 + p
{
    text-indent: 0;
}


p
{
    text-indent: 1.5em;
    margin: 0;
    padding: 0;
}


h1, h2, h3, h4, h5, h6
{
    color: black;
    font-family: Georgia, "Times New Roman", Times, serif;
}


span.corr, span.abbr, span.measure, abbr
{
    border-bottom: 0px;
	text-decoration: none;
}


.transcribernote
{
    background-color: transparant;
    border: white 0pt solid;
    font-size: 80%;
    font-family: Georgia, "Times New Roman", Times, serif;
}


.figureHead, .noteref, span.leftnote, p.legend, .versenum, .rightnote,	.pagenum, .linenum, .pagenum a
{
	color: black;
}
